import React from 'react';
//import Word from "../assets/Word";

class WordTable extends React.Component {  
  buildWordList = () => {
    console.log("WordList:::", this.props.wordList);
    if(this.props.wordList["type"] === "host") {
        return (
        <div>
          {this.props.wordList["wordList"].map(word => 
            <h1>1 - {word["word"]} - {word["guesses"]}</h1>,
            <div>
              {this.props.players.map(player => 
                <p>{player["name"]}</p>
              )}    
               </div>
          )}
        </div>
      )}
    if(this.props.wordList["type"] === "player") {
      return (
        <div>
          {this.props.wordList["wordList"].map(word => 
            {
              console.log(word)
              if(word["visible"]) {
                return <h1>1 - {word["word"]} - {word["guesses"]}</h1>
              }
              else {
                return <h1>???? - {this.props.wordList["type"]}</h1>
              }
            }   
          )}
        </div>
      )}
  }

  render() {
    return (
      <div style={{ textAlign: "center" }}>
        {this.buildWordList()}
      </div>
    );
  }
}

export default WordTable;
