import React from 'react';
import {socket} from "../../service/socket";

class WordTable extends React.Component {  

  buildWordList = () => {
    console.log("WordList:::", this.props.data);
    console.log("Players:::", this.props);
    
    return (
        <div>
          {this.props.data["wordList"].map((word, index) => 
            this.buildWord(index, word, this.props.data["players"], this.props.data["type"]) 
          )}
        </div>
      )
  }

  toggleWordVisibility = (index) => {
    socket.emit('toggle word visibility', index);
  }

  buildWord = (index, word, players, type) => {
    console.log(word["visible"])
    console.log("visible".repeat(1))

    if(type === "host") {
      return (
        <div>
          <h1><span onClick={() => this.toggleWordVisibility(index)}>{word["word"]}</span> - {word["guesses"]}</h1>
          <div>{players.map(player => <span>{player["name"]} + </span>)} </div>
        </div>
      )
    }
    if(type === "player") {
        if(word["visible"]) {
          return (
            <div>
              <h1>?????</h1>
            </div>
            )        
        } else {
          return (
            <div>
              <h1>{word["word"]} - {word["guesses"]}</h1>
              <div>{players.map(player => <span>{player["name"]} + </span>)} </div>
            </div>
          )
        }
      }
    return <p>Problem loading game content</p>
  }

  render() {
    return (
      <div style={{ textAlign: "center" }}>
        {this.buildWordList()}
      </div>
    );
  }
}

export default WordTable;
