import React, { Component } from "react";
import {socket} from "./service/socket";

class App extends Component {
  constructor() {
    super();
    console.log('Connect')
    this.state = {
      color: 'white',
    };
  }

  // sending sockets
  send = () => {
    console.log('client')
    socket.emit('change color', this.state.color) 
  }

  ///

  // adding the function
  setColor = (color) => {
    console.log('Change Color')
    this.setState({ color })
  }

  ///

  // set cookie and login save id to server socket
  login = (event) => {
    event.preventDefault();
    console.log('logging in as ', event.target.name)
  }

  ///

  handleNameChange = (event) => {
    this.setState({name: event.target.value});
  }

  ///


  render() {
    console.log('client')
    socket.on('change color', (col) => {
      document.body.style.backgroundColor = col
    })
    return (
      <div>
        <div style={{ textAlign: "center" }}>
          <button onClick={() => this.send() }>Change Color</button>
          <button id="blue" onClick={() => this.setColor('blue')}>Blue</button> 
          <button id="red" onClick={() => this.setColor('red')}>Red</button>
        </div>
        <div style={{ textAlign: "center" }}>
          <form>
            <label>Player name:</label>
            <input type="text" name="name" onChange={this.handleNameChange}></input><br/>
            <input type="submit" onClick={this.login} name="host_login" value="Host"/>
            <input type="submit" onClick={this.login} name="player_login" value="Player"/>
          </form>
        </div>
      </div>
    )
  }
}
export default App;
