import React from 'react';
import {socket} from "../service/socket";
import Error from "./Error";
import HostScreen from "./host/HostScreen"
import PlayerScreen from "./player/PlayerScreen"

class Game extends React.Component {


  componentDidMount() {
    socket.on('load gamescreen', (type, name) => {
      this.setState({type: type, name: name});
      document.body.style.backgroundColor = 'green';
    })
  }

  loadPageContent = (player_type) => {
    const { cookies } = this.props; 
    socket.emit('load game page', cookies.get('name'), cookies.get('id'), player_type) 
  }

  render() {
    return (
      <div style={{ textAlign: "center" }}>
        Game Screen
        {this.state.type ===  "host" && <HostScreen />}
        {this.state.type ===  "player" && <PlayerScreen />} 
        {this.state.type ===  "player" && <PlayerScreen />} 
      </div>
    );
  }
}

export default Game;
