import React from 'react';

class WordTable extends React.Component {  

  buildWordList = () => {
    console.log("WordList:::", this.props.data);
    console.log("Players:::", this.props);
    
    if(this.props.data["type"] === "host") {
        return (
        <div>
          {this.props.data["wordList"].map((word, index) =>
            {
            console.log(index)
            this.buildWord(word, word["visible"], this.props.data["players"], this.props.data["type"]) 
            }
          )}
        </div>
      )}
    if(this.props.data["type"] === "player") {
      return (
        <div>
          {this.props.data["wordList"].map(word => 
            {
              console.log(word)
              if(word["visible"]) {
                return <h1>1 - {word["word"]} - {word["guesses"]}</h1>
              }
              else {
                return <h1>???? - {this.props.data["type"]}</h1>
              }
            }   
          )}
        </div>
      )}
  }

  buildWord = (word, isVisible, players, type) => {
    if(type === "host") {
      return (
        <div>
          <h1>{word["word"]} - {word["guesses"]}</h1>
          <div>{players.map(player => <span>{player["name"]} + </span>)} </div>
        </div>
      )
    }
    if(type === "player") {
        if(word["visible"]) {
          return (
            <div>
              <h1>{word["word"]} - {word["guesses"]}</h1>
              {players.map(player => <p>{player["name"]}</p>)} 
            </div>
          )
        } else {
          return (
            <div>
              <h1>{word["word"]} - {word["guesses"]}</h1>
              {players.map(player => <p>{player["name"]}</p>)} 
            </div>
          )
        }
      }
    return <p>Problem loading game content</p>
  }

  render() {
    return (
      <div style={{ textAlign: "center" }}>
        {this.buildWordList()}
      </div>
    );
  }
}

export default WordTable;
