import React from 'react';
//import Word from "../assets/Word";

class WordTable extends React.Component {  

  buildWordList = () => {
    console.log("WordList:::", this.props.wordList);
    console.log("Players:::", this.props.players);
    if(this.props.wordList["type"] === "host") {
        return (
        <div>
          {this.props.wordList["wordList"].map(word =>
            //<h1>1 - {word["word"]} - {word["guesses"]}</h1>
            this.buildWord(word, this.props.players, "host") 
          )}
        </div>
      )}
    if(this.props.wordList["type"] === "player") {
      return (
        <div>
          {this.props.wordList["wordList"].map(word => 
            {
              console.log(word)
              if(word["visible"]) {
                return <h1>1 - {word["word"]} - {word["guesses"]}</h1>
              }
              else {
                return <h1>???? - {this.props.wordList["type"]}</h1>
              }
            }   
          )}
        </div>
      )}
  }

  buildWord = (word, players, type) => {
    /*<h1>0 - {word["word"]} - {word["guesses"]}</h1>,
            <h2>Players</h2>*/
    console.log("P like players", players)
    return <div>
              <h1>{word["word"]} - {word["guesses"]}</h1>
              <p>Test</p>
           </div>
  }

  render() {
    return (
      <div style={{ textAlign: "center" }}>
        {this.buildWordList()}
      </div>
    );
  }
}

export default WordTable;
