import React from 'react';
import {socket} from "../../service/socket";
import WordTable from "../assets/WordTable"
import CurrentPlayers from "../host/CurrentPlayers"

class PlayerScreen extends React.Component {
  constructor() {
    super();
    this.state = {
      players: [],
      wordList: []
    };
    socket.emit('fetch wordlist')
  }
    
  componentDidMount() {
    socket.on('toggle screen', (s) => {
      console.log('Test')
      document.body.style.backgroundColor = 'green';
    })
    socket.on('update wordList', (words) => {
        console.log("Updating words", words)
        this.setState({wordList: words});
    })  
  }

  handleNameChange = (event) => {
    this.setState({name: event.target.value});
  }
  
  setPlayerList(playerList) {
    console.log("Player list in parent")
    console.log(playerList)
    this.setState({players: playerList});
  }

  render() {
    <CurrentPlayers data={{setPlayerList:this.setPlayerList.bind(this)}}/>
    return (
      <div style={{ textAlign: "center" }}>
        
        <WordTable data={{"wordList":this.state.wordList, "player":this.state.players, "type":"player"}} />
      </div>
    );
  }
}

export default PlayerScreen;
