import React from 'react';
import {socket} from "../../service/socket";
import CurrentPlayers from "./CurrentPlayers"
import WordMenu from "./WordMenu"
import WordTable from "../assets/WordTable"

//This screen will have a button for visibility for every list item. 
//If we click the button, it will update itself from hide to show and 
//it will send a message to the socket to update the content. 
//The socket will update the data on the server and will then emit the 
//Updated data to the player-room, where the screen of the players will 
//be rerendered.


//When a user loggs in, the host should get an updated list of the current users 
//(and he should be able to remove users)


//Stretch goal: Score counting

class HostScreen extends React.Component {

  constructor() {
    super();
    this.state = {
      players: [],
      wordList: []
    };
    socket.emit('fetch wordlist')
  }   
    
  componentDidMount() {
    socket.on('toggle screen', (s) => {
      console.log('Test')
    })
    socket.on('update wordList', (words) => {
        console.log("Updating words", words)
        this.setState({wordList: words});
    })  
  }

  setPlayerList(playerList) {
    console.log("Player list in parent")
    console.log(playerList)
    this.setState({players: playerList});
  }

  render() {
    return (
      <div>
        <CurrentPlayers data={{setPlayerList:this.setPlayerList.bind(this)}}/>
        <WordMenu/>
        <WordTable data={{"wordList":this.state.wordList, "players":this.state.players, "type":"host"}} />
      </div>
    );
  }
}

export default HostScreen;
